import React, { useState } from 'react';

function BookForm({ onAdd }) {
  const [form, setForm] = useState({ title: '', author: '', genre: '', year: '' });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({ ...form, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (form.title && form.author && form.genre && form.year) {
      onAdd(form);
      setForm({ title: '', author: '', genre: '', year: '' });
    } else {
      alert("Preencha todos os campos!");
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input name="title" value={form.title} onChange={handleChange} placeholder="Título" />
      <input name="author" value={form.author} onChange={handleChange} placeholder="Autor" />
      <input name="genre" value={form.genre} onChange={handleChange} placeholder="Gênero" />
      <input name="year" value={form.year} onChange={handleChange} placeholder="Ano da leitura" />
      <button type="submit">Adicionar Livro</button>
    </form>
  );
}

export default BookForm;
