// src/App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import NavBar from './components/NavBar';
import BookForm from './components/BookForm';
import BookList from './components/BookList';
import './styles/index.css';

function App() {
  const [books, setBooks] = useState([]);

  const addBook = (book) => setBooks([...books, book]);
  const deleteBook = (index) =>
    setBooks(books.filter((_, i) => i !== index));

  return (
    <Router>
      <NavBar />
      <div className="main-content">
        <Routes>
          <Route path="/" element={<p>Bem-vindo ao seu Di√°rio de Leitura!</p>} />
          <Route path="/add" element={<BookForm onAddBook={addBook} />} />
          <Route path="/list" element={<BookList books={books} onDeleteBook={deleteBook} />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
